import React from 'react';

class SitesDropDown extends React.Component {

    constructor(props) {
        super(props);
        this.state = { sites: [], siteSelected: "", paymentMethods: [], paymentSelected: ""};

        this.change = this.change.bind(this)

    }

    componentDidMount() {
        let initialSites = [];
        fetch('http://localhost:9001/sites')
            .then(response => {
                return response.json();
            }).then(data => {
            initialSites = data.map((site) => {
                return site
            });
            this.setState({
                sites: initialSites,
            });
        });
    }

    getPaymentMethods(siteId) {
        let initialPayments = [];

        console.log(siteId)
        fetch('http://localhost:9001/sites/' + siteId)
            .then(response => {
                return response.json();
            }).then(data => {
            initialPayments = data.map((payment) => {
                return payment
            });
            this.setState({
                paymentMethods: initialPayments,
            });
            initialPayments.forEach(a => console.log("nombre:" + a.name + " - id" + a.id))
        });
    }

    change(event){

        this.setState({
            siteSelected: event.target.value,
        });

        this.getPaymentMethods(event.target.value)
    }

    render(){
        return <div className="drop-down">
            <p>Seleccione un pa√≠s</p>
            <select onChange={this.change} value={this.state.siteSelected}>
                {
                    this.state.sites.sort().map((obj) => {
                    return <option value={obj.id}>{obj.name}</option>
                    })
                }

            </select>
        </div>;
    }
}

export default SitesDropDown;
