import React, { Component } from 'react';
import { AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-balham.css';
import { Button } from 'reactstrap';
import axios from 'axios';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';



class Agencies extends Component {


    constructor(props) {
        super(props);
        this.state = {
            sites: [],
            siteSelected: "",
            paymentMethods: [],
            paymentSelected: "",
            defLatitudeSelected: "",
            defLongitudeSelected: "",
            radius:"300",
            disableDefault: false,
            defaultLocationSelected: "",
            defaultLocations: [
                {
                    id:1,
                    name: 'Córdoba - Plaza San Martín',
                    latitude: '-31.4167611',
                    longitude: '-64.183927'
                },
                {
                    id:2,
                    name: 'Córdoba - Plaza Colón',
                    latitude: '-31.4094088',
                    longitude: '-64.1957464'
                },
                {
                    id:3,
                    name: 'Córdoba - Plaza Jerónimo del Barco',
                    latitude: '-31.403866',
                    longitude: '-64.218144'
                },
                {
                    id:4,
                    name: 'Córdoba - Parque de las Naciones',
                    latitude: '-31.3834727',
                    longitude: '-64.2278911'
                },
                {
                    id:5,
                    name: 'Córdoba - Plaza España',
                    latitude: '-31.4286973',
                    longitude: '-64.1849783'
                },
                {
                    id:6,
                    name: 'Córdoba - Hipódromo',
                    latitude: '-31.4534822',
                    longitude: '-64.1835352'
                },
            ],
            sortCriteriaSelected: "",
            sortCriterias:[
                {
                    id: 'address_line',
                    name: 'Dirección'
                },
                {
                    id: 'agency_code',
                    name: 'Código de agencia'
                },
                {
                    id: 'distance',
                    name: 'Distancia'
                }

            ],
            columnDefs: [
                {headerName: "Código", field: "agency_code"},
                {headerName: "Descripción", field: "description"},
                {headerName: "Dirección", field: "address_line"},
                {headerName: "Ciudad", field: "city"},
                {headerName: "Distancia", field: "distance"},
                {headerName: "Fav", field: "fav", width: 50, cellRenderer: (params) => {
                        var img = document.createElement("img");
                        img.src = "https://img.icons8.com/cute-clipart/24/000000/thumb-up.png"
                        img.addEventListener('click', (e) => {
                            e.preventDefault();
                            this.addToFavorites(params.data)
                        });
                        return img;
                    }},
                {headerName: "Unfav", field: "unfav", width: 60 , cellRenderer: (params) => {
                        var img = document.createElement("img");
                        img.src = "https://img.icons8.com/cute-clipart/24/000000/thumbs-down.png"
                        img.addEventListener('click', (e) => {
                            e.preventDefault();
                            this.removeFromFavorites(params.data)
                        });
                        return img;
                    }}
            ],
            rowData: []
        };

        this.onSiteChange = this.onSiteChange.bind(this)
        this.onPaymentChange = this.onPaymentChange.bind(this)
        this.onDefaultLocationsChange = this.onDefaultLocationsChange.bind(this)
        this.onFetchAgencies = this.onFetchAgencies.bind(this)
        this.onSortCriteriaChanged = this.onSortCriteriaChanged.bind(this)
        this.onLatitudeChange = this.onLatitudeChange.bind(this)
        this.onLongitudeChange = this.onLongitudeChange.bind(this)
        this.onRadiusChange = this.onRadiusChange.bind(this)

    }

    componentDidMount() {
        let initialSites = [];
        fetch('http://localhost:9001/sites')
            .then(response => {
                return response.json();
            }).then(data => {
            initialSites = data.map((site) => {
                return site
            });
            this.setState({
                sites: initialSites,
            });
        });
    }

    async addToFavorites(agency){
        var body = JSON.stringify(agency)

        const response = await axios.post(
            'http://localhost:9001/sites/write',
            { example: 'data' },
            { headers: { 'Content-Type': 'application/json' },
                     data: {body}
            }
        )

        alert(response.data)
    }

    async removeFromFavorites(agency){
        var body = JSON.stringify(agency)

        const response = await axios.delete(
            'http://localhost:9001/sites/remove/'+agency.agency_code)

        alert(response.data)
    }

    getPaymentMethods(siteId) {
        let initialPayments = [];

        fetch('http://localhost:9001/sites/' + siteId)
            .then(response => {
                return response.json();
            }).then(data => {
            initialPayments = data.map((payment) => {
                return payment
            });
            this.setState({
                paymentMethods: initialPayments,
            });
        });
    }

    onSiteChange(event){

        if (event.target.value == "MLA") {
            this.setState({
                siteSelected: event.target.value,
                disableDefault: false
            });
        } else {
            this.setState({
                siteSelected: event.target.value,
                disableDefault: true
            });
        }
        this.getPaymentMethods(event.target.value)
    }

    onPaymentChange(event){
        this.setState({
            paymentSelected: event.target.value,
        });
    }

    onDefaultLocationsChange(event){
        var latitude = this.state.defaultLocations[event.target.value -1].latitude
        var longitude = this.state.defaultLocations[event.target.value -1].longitude

        this.setState({
            defLatitudeSelected: latitude,
            defLongitudeSelected: longitude
        });
    }

    onFetchAgencies(){
        var site = this.state.siteSelected
        var paymentMethod = this.state.paymentSelected
        var latitude = this.state.defLatitudeSelected
        var longitude = this.state.defLongitudeSelected
        var radius = this.state.radius
        var sortCriteria = this.state.sortCriteriaSelected

        let initialAgencies = [];
        fetch('http://localhost:9001/sites/MLA/rapipago/-31.4167611/-64.183927/3000/distance')
        //fetch('http://localhost:9001/sites/'+site+'/'+paymentMethod+'/'+latitude+'/'+longitude+'/'+radius+'/'+sortCriteria)
            .then(response => {
                return response.json();
            }).then(data => {
            initialAgencies = data.map((site) => {
                return site
            });
            this.setState({
                rowData: initialAgencies,
            });
        });


    }

    onSortCriteriaChanged(event) {
        this.setState({
            sortCriteriaSelected: event.target.value
        })
    }

    onLatitudeChange(event) {
        this.setState({
            defLatitudeSelected: event.target.value
        })
    }

    onLongitudeChange(event) {
        this.setState({
            defLongitudeSelected: event.target.value
        })
    }

    onRadiusChange(event) {
        this.setState({
            radius: event.target.value
        })
    }

    onDefaultLocationsChange(event){
        var latitude = this.state.defaultLocations[event.target.value -1].latitude
        var longitude = this.state.defaultLocations[event.target.value -1].longitude

        this.setState({
            defLatitudeSelected: latitude,
            defLongitudeSelected: longitude
        });
    }

    render(){
        return (
            <div className="form">
                <form onSubmit={this.onFetchAgencies}>
                <p>Seleccione un país</p>
                <select onChange={this.onSiteChange} value={this.state.siteSelected}>
                    {
                        this.state.sites.sort().map((obj) => {
                            return <option value={obj.id}>{obj.name}</option>
                        })
                    }
                </select>
                <p>Seleccione un método de pago</p>
                <select onChange={this.onPaymentChange} value={this.state.paymentSelected}>
                    {
                        this.state.paymentMethods.map((obj) => {
                            return <option value={obj.id}>{obj.name}</option>
                        })
                    }
                </select>
                <p>Ubicaciones por defecto (Sólo disponible para Argentina)</p>
                <select disabled={this.state.disableDefault} onChange={this.onDefaultLocationsChange} value={this.state.defaultLocationSelected}>
                    {
                        this.state.defaultLocations.map((obj) => {
                            return <option value={obj.id}>{obj.name}</option>
                        })
                    }
                </select>
                <p></p>

                    <label>
                        Ubicación (latitud,longitud, radio):
                        <input id="latitude" type="text" value={this.state.defLatitudeSelected} onChange={this.onLatitudeChange}/>
                        <input id="longitude" type="text" value={this.state.defLongitudeSelected} onChange={this.onLongitudeChange}/>
                        <input id="radius" type="text" value={this.state.radius} onChange={this.onRadiusChange}/>
                    </label>
                    <p>Ordenar por:</p>
                    <select onChange={this.onSortCriteriaChanged} value={this.state.sortCriteriaSelected}>
                        {
                            this.state.sortCriterias.map((obj) => {
                                return <option value={obj.id}>{obj.name}</option>
                            })
                        }
                    </select>
                    <p></p>
                    <Button value="Consultar" onClick={this.onFetchAgencies}>Consultar</Button>
                </form>

                <p></p>

                <div
                    className="ag-theme-balham"
                    style={{ height: '200px', width: '1110px' }}
                >
                    <AgGridReact
                        columnDefs={this.state.columnDefs}
                        rowData={this.state.rowData}>
                    </AgGridReact>
                </div>
            </div>
        )
    }

}

export default Agencies;
