import React from 'react';

class SitesDropDown extends React.Component {

    constructor(props) {
        super(props);
        this.state = { sites: [] };

    }

    componentWillMount() {
        let initialSites = [];
        fetch('http://localhost:9001/sites')
            .then(response => {
                return response.json();
            }).then(data => {
            initialSites = data.map((site) => {
                return site
            });
            this.setState({
                sites: initialSites,
            });
        });
    }

    onChangeFunc(event){

        console.log("call")
        this.setState({value: event.target.value});
    }

    render(){
        return <div className="drop-down">
            <p>Seleccione un pa√≠s</p>
            <select>
                {
                    this.state.sites.map((obj) => {
                    return <option value={obj.id}>{obj.name}</option>
                    })
                }
                onChange={(val)=> {this.onChangeFunc({target: { name:'revision_id', value: val.value }})}}

            </select>
        </div>;
    }
}

export default SitesDropDown;
