import React, { Component } from 'react';
import { Button, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';

class Agencies extends Component {


    constructor(props) {
        super(props);
        this.state = {
            sites: [],
            siteSelected: "",
            paymentMethods: [],
            paymentSelected: "",
            defLocationSelected: "",
            defaultLocations: [
                {
                    name: 'Córdoba - Plaza San Martín',
                    location: '-31.4167611,-64.183927'
                },
                {
                    name: 'Córdoba - Plaza Colón',
                    location: '-31.4094088,-64.1957464'
                },
                {
                    name: 'Córdoba - Plaza Jerónimo del Barco',
                    location: '-31.403866,-64.218144'
                },
                {
                    name: 'Córdoba - Parque de las Naciones',
                    location: '-31.3834727,-64.2278911'
                },
                {
                    name: 'Córdoba - Plaza España',
                    location: '-31.4286973,-64.1849783'
                },
                {
                    name: 'Córdoba - Hipódromo',
                    location: '-31.4534822,-64.1835352'
                },
            ]
        };

        this.onSiteChange = this.onSiteChange.bind(this)
        this.onPaymentChange = this.onPaymentChange.bind(this)
        this.onDefaultLocationsChange = this.onDefaultLocationsChange.bind(this)

    }



    componentDidMount() {
        let initialSites = [];
        fetch('http://localhost:9001/sites')
            .then(response => {
                return response.json();
            }).then(data => {
            initialSites = data.map((site) => {
                return site
            });
            this.setState({
                sites: initialSites,
            });
        });
    }

    getPaymentMethods(siteId) {
        let initialPayments = [];

        fetch('http://localhost:9001/sites/' + siteId)
            .then(response => {
                return response.json();
            }).then(data => {
            initialPayments = data.map((payment) => {
                return payment
            });
            this.setState({
                paymentMethods: initialPayments,
            });
        });
    }

    onSiteChange(event){

        this.setState({
            siteSelected: event.target.value,
        });

        this.getPaymentMethods(event.target.value)
    }

    onPaymentChange(event){

        this.setState({
            paymentSelected: event.target.value,
        });
    }

    onDefaultLocationsChange(event){
        console.log(event.target.value)

        this.setState({
            defLocationSelected: event.target.value,
        });
    }

    render(){
        return (
            <div className="drop-down">
                <p>Seleccione un país</p>
                <select onChange={this.onSiteChange} value={this.state.siteSelected}>
                    {
                        this.state.sites.sort().map((obj) => {
                            return <option value={obj.id}>{obj.name}</option>
                        })
                    }
                </select>
                <p>Seleccione un método de pago</p>
                <select onChange={this.onPaymentChange} value={this.state.paymentSelected}>
                    {
                        this.state.paymentMethods.map((obj) => {
                            return <option value={obj.id}>{obj.name}</option>
                        })
                    }
                </select>
                <p>Ubicaciones por defecto (Sólo disponible para Argentina)</p>
                <select onChange={this.onDefaultLocationsChange} value={this.state.defLocationSelected}>
                    {
                        this.state.defaultLocations.map((obj) => {
                            return <option value={obj.name}>{obj.name}</option>
                        })
                    }
                </select>
                <p></p>
                <form>
                    <label>
                        Ubicación:
                        <input id="location" type="text" value={this.state.defLocationSelected.location} onChange={this.handleChange} />
                    </label>
                    <p></p>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }

}

export default Agencies;
