import React, { Component } from 'react';
import { Button, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';

class Sites extends Component {

    state = { theme: null, dropdownOpen: false, sites: "" }

    toggleDropdown = () => {
        this.setState({ dropdownOpen: !this.state.dropdownOpen });
    }

    resetTheme = evt => {
        evt.preventDefault();
        this.setState({ theme: null });
    }

    chooseTheme = (theme, evt) => {
        evt.preventDefault();
        this.setState({ theme });
    }

    getSites() {
        fetch("http://localhost:9001/sites")
            .then(res => res.text())
            .then(res => this.setState({ sites: res }))
            .catch(err => err);
    }

    componentWillMount() {
        this.getSites();
    }

    onChange(event) {
        this.setState({ siteselected: event.value });
        console.log('Selected site:', event.value);
    }

    render() {

        const { theme, dropdownOpen } = this.state;
        const themeClass = theme ? theme.toLowerCase() : 'secondary';

        return (
            <div className="d-flex flex-wrap justify-content-center position-absolute w-100 h-100 align-items-center align-content-center">

                <div>
                    {['clientName', 'siteName', 'segmentName'].map(key => (
                        <select key={key}>
                            {this.state.data.map(({ [key]: value }) => <option key={value}>{value}</option>)}
                        </select>
                    ))}
                </div>

            </div>
        );

    }

}

export default Sites;
