import React from 'react';

class SitesDropDown extends React.Component {

    constructor(props) {
        super(props);
        this.state = { sites: [] };

    }

    componentWillMount() {
        let initialSites = [];
        fetch('http://localhost:9001/sites')
            .then(response => {
                return response.json();
            }).then(data => {
            initialSites = data.map((site) => {
                return site
            });
            this.setState({
                sites: initialSites,
            });
        });
    }

    onOptionChange(id, event){

        alert(event)
        this.setState({value: event.target.value});
    }

    render(){
        return <div className="drop-down">
            <p>Seleccione un pa√≠s</p>
            <select>
                {
                    this.state.sites.sort().map((obj) => {
                    return <option value={obj.id}>{obj.name}</option>
                    })
                }
                onChange={e => this.onOptionChange(e)}

            </select>
        </div>;
    }
}

export default SitesDropDown;
