import React from 'react';

class SitesDropDown extends React.Component {

    constructor(props) {
        super(props);
        this.state = { sites: [], siteSelected: "", paymentMethods: [], paymentSelected: ""};

        this.change = this.change.bind(this)

    }

    componentWillMount() {
        let initialSites = [];
        fetch('http://localhost:9001/sites')
            .then(response => {
                return response.json();
            }).then(data => {
            initialSites = data.map((site) => {
                return site
            });
            this.setState({
                sites: initialSites,
            });
        });
    }

    getPaymentMethods() {
        let initialPayments = [];
        fetch('http://localhost:9001/sites/' + this.state.siteSelected)
            .then(response => {
                return response.json();
            }).then(data => {
            initialPayments = data.map((payment) => {
                return payment
            });
            this.setState({
                paymentMethods: initialPayments,
            });
        });

        return (
            <div className="drop-down">
                <p>Seleccione un método de pago</p>
                <select onChange={this.change} value={this.state.paymentSelected}>
                    {
                        this.state.paymentMethods.sort().map((obj) => {
                            return <option value={obj.id}>{obj.name}</option>
                        })
                    }

                </select>
            </div>
        )
    }

    change(event){
        this.setState({
            siteSelected: event.target.value,
        });

        this.getPaymentMethods()
    }

    render(){
        return <div className="drop-down">
            <p>Seleccione un país</p>
            <select onChange={this.change} value={this.state.siteSelected}>
                {
                    this.state.sites.sort().map((obj) => {
                    return <option value={obj.id}>{obj.name}</option>
                    })
                }

            </select>
        </div>;
    }
}

export default SitesDropDown;
