import React, { Component } from 'react';

class Agencies extends Component {


    constructor(props) {
        super(props);
        this.state = {
            sites: [],
            siteSelected: "",
            paymentMethods: [],
            paymentSelected: "",
            defLocationSelected: "",
            disableDefault: false,
            defaultLocations: [
                {
                    name: 'Córdoba - Plaza San Martín',
                    location: '-31.4167611,-64.183927'
                },
                {
                    name: 'Córdoba - Plaza Colón',
                    location: '-31.4094088,-64.1957464'
                },
                {
                    name: 'Córdoba - Plaza Jerónimo del Barco',
                    location: '-31.403866,-64.218144'
                },
                {
                    name: 'Córdoba - Parque de las Naciones',
                    location: '-31.3834727,-64.2278911'
                },
                {
                    name: 'Córdoba - Plaza España',
                    location: '-31.4286973,-64.1849783'
                },
                {
                    name: 'Córdoba - Hipódromo',
                    location: '-31.4534822,-64.1835352'
                },
            ],
            searchCriteriaSelected: "",
            searchCriterias:[
                {
                    id: 'address_line',
                    name: 'Dirección'
                },
                {
                    id: 'agency_code',
                    name: 'Código de agencia'
                },
                {
                    id: 'distance',
                    name: 'Distancia'
                }

            ]

        };

        this.onSiteChange = this.onSiteChange.bind(this)
        this.onPaymentChange = this.onPaymentChange.bind(this)
        this.onDefaultLocationsChange = this.onDefaultLocationsChange.bind(this)
        this.onFetchAgencies = this.onFetchAgencies.bind(this)

    }



    componentDidMount() {
        let initialSites = [];
        fetch('http://localhost:9001/sites')
            .then(response => {
                return response.json();
            }).then(data => {
            initialSites = data.map((site) => {
                return site
            });
            this.setState({
                sites: initialSites,
            });
        });
    }

    getPaymentMethods(siteId) {
        let initialPayments = [];

        fetch('http://localhost:9001/sites/' + siteId)
            .then(response => {
                return response.json();
            }).then(data => {
            initialPayments = data.map((payment) => {
                return payment
            });
            this.setState({
                paymentMethods: initialPayments,
            });
        });
    }

    onSiteChange(event){

        if (event.target.value == "MLA") {
            this.setState({
                siteSelected: event.target.value,
                disableDefault: false
            });
        } else {
            this.setState({
                siteSelected: event.target.value,
                disableDefault: true
            });
        }
        this.getPaymentMethods(event.target.value)
    }

    onPaymentChange(event){

        this.setState({
            paymentSelected: event.target.value,
        });
    }

    onDefaultLocationsChange(event){
        console.log(event.target.value)

        this.setState({
            defLocationSelected: event.target.value,
        });
    }

    onFetchAgencies(){

    }

    render(){
        return (
            <div className="drop-down">
                <p>Seleccione un país</p>
                <select onChange={this.onSiteChange} value={this.state.siteSelected}>
                    {
                        this.state.sites.sort().map((obj) => {
                            return <option value={obj.id}>{obj.name}</option>
                        })
                    }
                </select>
                <p>Seleccione un método de pago</p>
                <select onChange={this.onPaymentChange} value={this.state.paymentSelected}>
                    {
                        this.state.paymentMethods.map((obj) => {
                            return <option value={obj.id}>{obj.name}</option>
                        })
                    }
                </select>
                <p>Ubicaciones por defecto (Sólo disponible para Argentina)</p>
                <select disabled={this.state.disableDefault} onChange={this.onDefaultLocationsChange} value={this.state.defLocationSelected}>
                    {
                        this.state.defaultLocations.map((obj) => {
                            return <option value={obj.location}>{obj.name}</option>
                        })
                    }
                </select>
                <p></p>
                <form onSubmit={this.onFetchAgencies}>
                    <label>
                        Ubicación (latitud,longitud):
                        <input id="location" type="text" value={this.state.defLocationSelected}/>
                    </label>
                    <p></p>
                    <input type="submit" value="Consultar" />
                </form>

                <div>
                    <h1>
                        2019 Sedans (React)
                    </h1>
                    <p>
                        Sort by model and price by clicking the column headers.
                        Filter by value or condition by clicking the filter icons
                        in the column headers.
                        Collapse and expand makes to see all the models.
                        Select one or more models with the mouse or keyboard and
                        copy your selection to the clipboard.</p>
                    <p>
                        Showing{' '}
                        <b>{this.state.data.items.length}</b> models from{' '}
                        <b>{this.state.data.groups.length}</b> makes.</p>
                    <FlexGrid
                        allowResizing={'None'}
                        showAlternatingRows={false}
                        isReadOnly={true}
                        selectionMode={'ListBox'}
                        headersVisibility={'Column'}
                        autoGenerateColumns={false}
                        columns={[
                            {binding:'make', header:'Make', width:"*", visible:false},
                            {binding:'model', header:'Model', width:"*"},
                            {binding:'price', header:'Price', width:".5*", format: 'c0',
                                aggregate: 'Avg'},
                        ]}
                        itemsSource={this.state.data}
                    >
                        <FlexGridFilter
                            filterApplied={this.filterApplied}
                        />
                    </FlexGrid>
                </div>

            </div>
        )
    }

}

export default Agencies;
